import React from "react";
import { createMuiTheme, responsiveFontSizes } from "@material-ui/core/styles";
import { ThemeProvider } from "@material-ui/styles";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import StepContent from "@material-ui/core/StepContent";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Link from "@material-ui/core/Link";
import Countdown from "./Countdown.js"; // https://medium.com/@kristin_baumann/react-countdown-6455838b6faf

let theme2 = createMuiTheme();
theme2 = responsiveFontSizes(theme2);

//const currentDate = new Date();
//const year = (currentDate.getMonth() === 11 && currentDate.getDate() > 23) ? currentDate.getFullYear() + 1 : currentDate.getFullYear();
const tomorrow = new Date(new Date().getTime() + 24 * 60 * 60 * 1000);

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%"
  },
  button: {
    marginTop: theme.spacing(1),
    marginRight: theme.spacing(1)
  },
  headline: {
    maxWidth: "860px",
    lineHeight: "1.2"
  },
  p1: {
    marginBottom: theme.spacing(1)
  },
  p2: {
    marginBottom: theme.spacing(2)
  },
  p2b: {
    maxWidth: "768px",
    marginTop: theme.spacing(4),
    marginBottom: theme.spacing(2)
  },
  p2b2: {
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2)
  },
  stepperContainer: {
    paddingLeft: "0",
    paddingRight: "0"
  },
  cardContent: {
    padding: theme.spacing(1)
  },
  actionsContainer: {
    marginBottom: theme.spacing(2)
  },
  resetContainer: {
    padding: theme.spacing(3)
  },
  card: {
    maxWidth: "768px",
    marginBottom: theme.spacing(2)
  },
  imageCard: {
    maxWidth: "100%"
  },
  media: {
    height: 140
  }
}));

function Step0() {
  const classes = useStyles();
  return (
    <React.Fragment>
      <Typography className={classes.p2b2} variant="h5" component="h2">
        <Countdown date={`${tomorrow}`} />
      </Typography>
      <Typography className={classes.p2b} variant="h5" component="h2">
        <u>
          <strong>Remember</strong>
        </u>
        &nbsp;ClickFunnels Gives You <strong>EVERYTHING</strong> You Need To
        Market, Sell and Deliver Your Products Online!
      </Typography>
      <Typography className={classes.p2b} variant="h5" component="h2">
        You're just one funnel away...
      </Typography>
    </React.Fragment>
  );
}

function Step1() {
  const classes = useStyles();
  return (
    <React.Fragment>
      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b2} variant="h5" component="h2">
          <strong>SECRET:</strong> ClickFunnels should <u>NEVER</u> cost you
          money...
        </Typography>
      </ThemeProvider>
      <Card className={classes.card}>
        <CardActionArea>
          <CardContent className={classes.cardContent}>
            <Link
              href="https://www.clickfunnels.com/?cf_affiliate_id=831693&affiliate_id=831693"
              target="_blank"
            >
              <img
                className={classes.imageCard}
                src="https://uploads.codesandbox.io/uploads/user/0b22c377-e160-4221-9087-53627a7647db/T36q-clickfunnels-14daysFREE-1c.png"
                alt=""
              />
            </Link>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button
            variant="contained"
            color="primary"
            href="https://www.clickfunnels.com/?cf_affiliate_id=831693&affiliate_id=831693"
            target="_blank"
          >
            Start Your 14 Day Free Trial
          </Button>
        </CardActions>
      </Card>

      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b} variant="h5" component="h2">
          ... ClickFunnels always over delivers. <em>ALWAYS...</em>
        </Typography>
      </ThemeProvider>

      <Card className={classes.card}>
        <CardActionArea>
          <CardContent className={classes.cardContent}>
            <Link
              href="https://www.clickfunnels.com/?cf_affiliate_id=831693&affiliate_id=831693"
              target="_blank"
            >
              <img
                className={classes.imageCard}
                src="https://uploads.codesandbox.io/uploads/user/0b22c377-e160-4221-9087-53627a7647db/oGiY-clickfunnels-14daysFREE-2.png"
                alt=""
              />
            </Link>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button
            variant="contained"
            color="primary"
            href="https://www.clickfunnels.com/?cf_affiliate_id=831693&affiliate_id=831693"
            target="_blank"
          >
            Start Your 14 Day Free Trial
          </Button>
        </CardActions>
      </Card>

      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b} variant="h5" component="h2">
          ... Sign-Up Takes Less Than 60 Seconds!
        </Typography>
      </ThemeProvider>

      <Card className={classes.card}>
        <CardActionArea>
          <CardContent className={classes.cardContent}>
            <Link
              href="https://www.clickfunnels.com/?cf_affiliate_id=831693&affiliate_id=831693"
              target="_blank"
            >
              <img
                className={classes.imageCard}
                src="https://uploads.codesandbox.io/uploads/user/0b22c377-e160-4221-9087-53627a7647db/Z8Lc-clickfunnels-14daysFREE-3b.png"
                alt=""
              />
            </Link>
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button
            variant="contained"
            color="primary"
            href="https://www.clickfunnels.com/?cf_affiliate_id=831693&affiliate_id=831693"
            target="_blank"
          >
            Start Your 14 Day Free Trial
          </Button>
        </CardActions>
      </Card>

      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b} variant="h5" component="h2">
          Don't move on until you've got your FREE ClickFunnels account.
        </Typography>
        <Typography className={classes.p2b} variant="h5" component="h2">
          You have everything to gain, and nothing to lose.
        </Typography>

        <Typography className={classes.p2b} variant="h5" component="h2">
          So stop procrastinating. Just do it!
        </Typography>

        <Button
          variant="contained"
          color="primary"
          href="https://www.clickfunnels.com/?cf_affiliate_id=831693&affiliate_id=831693"
          target="_blank"
        >
          Start Your 14 Day Free Trial (Seriously)
        </Button>
      </ThemeProvider>

      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b} variant="h5" component="h2">
          . <br />
          <br />
          . <br />
          <br />
          . <br />
          <br />
          <span role="img" aria-label="celebrate">
            🎉
          </span>{" "}
          You're now ready for Secret 2!
        </Typography>
      </ThemeProvider>
    </React.Fragment>
  );
}

function Step2() {
  const classes = useStyles();
  return (
    <React.Fragment>
      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b2} variant="h5" component="h2">
          <strong>SECRET:</strong> ClickFunnels hides thier <u>BEST</u> offer
          behind these two webinars...
        </Typography>
      </ThemeProvider>
      <Card className={classes.card}>
        <CardActionArea>
          <CardContent className={classes.cardContent}>
            <img
              className={classes.imageCard}
              src="https://uploads.codesandbox.io/uploads/user/0b22c377-e160-4221-9087-53627a7647db/wYWd-clickfunnels-14daysFREE-1b.png"
              alt=""
            />
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button variant="outlined" color="primary">
            Start Your 14 Day Free Trial
          </Button>
        </CardActions>
      </Card>

      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b} variant="h5" component="h2">
          ... ClickFunnels always over delivers. <em>ALWAYS...</em>
        </Typography>
      </ThemeProvider>

      <Card className={classes.card}>
        <CardActionArea>
          <CardContent className={classes.cardContent}>
            <img
              className={classes.imageCard}
              src="https://uploads.codesandbox.io/uploads/user/0b22c377-e160-4221-9087-53627a7647db/oGiY-clickfunnels-14daysFREE-2.png"
              alt=""
            />
          </CardContent>
        </CardActionArea>
        <CardActions>
          <Button variant="outlined" color="primary">
            Start Your 14 Day Free Trial
          </Button>
        </CardActions>
      </Card>

      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b} variant="h5" component="h2">
          Don't move on until you've got your FREE ClickFunnels account.
        </Typography>
        <Typography className={classes.p2b} variant="h5" component="h2">
          You have everything to gain, and nothing to lose.
        </Typography>

        <Typography className={classes.p2b} variant="h5" component="h2">
          So stop procrastinating. Take action!
        </Typography>

        <Button variant="outlined" color="primary">
          Start Your 14 Day Free Trial (Seriously)
        </Button>
      </ThemeProvider>

      <ThemeProvider theme={theme2}>
        <Typography className={classes.p2b} variant="h5" component="h2">
          <span role="img" aria-label="celebrate">
            🎉
          </span>{" "}
          You're now ready for Secret 2!
        </Typography>
      </ThemeProvider>
    </React.Fragment>
  );
}

function Step3() {
  return <Typography>Step 3</Typography>;
}

function Step4() {
  return <Typography>Step 4</Typography>;
}

function Step5() {
  return <Typography>Step 5</Typography>;
}

function StepUnknown() {
  return <Typography>Step Unknown</Typography>;
}

function getSteps() {
  return [
    "The clock is ticking...",
    "SECRET 1: FREE for 14 Days",
    "SECRET 2: FREE for 12 Months",
    "SECRET 3: FREE for LIFE!",
    "BONUS 1: Funnel Hack Russell",
    "BONUS 2: Your Share Funnel",
    "BONUS 3: Community & Accountability"
  ];
}

function getStepContent(step) {
  switch (step) {
    case 0:
      return <Step0 />;
    case 1:
      return <Step1 />;
    case 2:
      return <Step2 />;
    case 3:
      return <Step3 />;
    case 4:
      return <Step4 />;
    case 5:
      return <Step5 />;
    default:
      return <StepUnknown />;
  }
}

function VerticalLinearStepper() {
  const classes = useStyles();
  const [activeStep, setActiveStep] = React.useState(0);
  const steps = getSteps();

  function handleNext() {
    //console.log('next');
    setActiveStep(prevActiveStep => prevActiveStep + 1);
  }

  function handleBack() {
    setActiveStep(prevActiveStep => prevActiveStep - 1);
  }

  function handleReset() {
    setActiveStep(0);
  }

  return (
    <div className={classes.root}>
      <Typography>&nbsp;</Typography>
      <ThemeProvider theme={theme2}>
        <Typography className={classes.headline} variant="h2" component="h1">
          Get a <b>FREE</b> ClickFunnels Account in 24 Hours (3 Secrets
          Exposed!)
        </Typography>
      </ThemeProvider>
      <Stepper
        className={classes.stepperContainer}
        activeStep={activeStep}
        orientation="vertical"
      >
        {steps.map((label, index) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
            <StepContent>
              {getStepContent(index)}
              <div className={classes.actionsContainer}>
                <div>
                  <Button
                    variant="outlined"
                    disabled={activeStep === 0}
                    onClick={handleBack}
                    className={classes.button}
                  >
                    Back
                  </Button>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleNext}
                    className={classes.button}
                  >
                    {activeStep === steps.length - 1 ? "Finish" : "Next"}
                  </Button>
                </div>
              </div>
            </StepContent>
          </Step>
        ))}
      </Stepper>
      {activeStep === steps.length && (
        <Paper square elevation={0} className={classes.resetContainer}>
          <Typography>All steps completed - you&apos;re finished</Typography>
          <Button onClick={handleReset} className={classes.button}>
            Reset
          </Button>
        </Paper>
      )}
    </div>
  );
}

export default VerticalLinearStepper;
